import { Directive, Input } from '@angular/core';
import { CoerceBoolean } from '../../core/common/coerce-boolean-inputs.decorator';

@Directive({
  selector: 'button[adrButton]',
  host: {
    class: 'adr-button',
    '[class.adr-small]': 'size === "small"',
    '[class.adr-medium]': 'size === "medium"',
    '[class.adr-large]': 'size === "large"',
    '[class.adr-outlined]': 'outlined',
    '[class.adr-primary]': 'color === "primary"',
    '[class.adr-accent]': 'color === "accent"',
    '[class.adr-warn]': 'color === "warn"'
  }
})
export class ButtonDirective {
  @Input()
  public size: 'small' | 'medium' | 'large' = 'medium';

  @Input()
  @CoerceBoolean()
  public outlined?: boolean;

  @Input()
  public color: 'primary' | 'accent' | 'warn' = 'primary';
}

import { Directive, EventEmitter, HostBinding, HostListener, Input, Output } from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';
import { AutoUnsubscribe } from '../../../core/common/auto-unsubscribe.decorator';
import { CoerceBoolean } from '../../../core/common/coerce-boolean-inputs.decorator';

@Directive({
  selector: 'input[adrInput]',
  host: {
    class: 'adr-input',
    '[class.adr-focused]': 'focused || null',
    '[class.adr-readonly]': 'readonly || null',
    '[class.adr-disabled]': 'disabled || null'
  },
  providers: [{ provide: NG_VALUE_ACCESSOR, useExisting: InputDirective, multi: true }]
})
@AutoUnsubscribe()
export class InputDirective implements ControlValueAccessor {
  @HostBinding('attr.value')
  @Input()
  public value: any;

  @Input()
  @HostBinding('readonly')
  @CoerceBoolean()
  public readonly: boolean = false;

  @Input()
  @HostBinding('disabled')
  @CoerceBoolean()
  public disabled: boolean = false;

  @Output()
  public readonly valueChange: EventEmitter<any> = new EventEmitter<any>();

  private _focused: boolean = false;

  get focused(): boolean {
    return this._focused;
  }

  public writeValue(value: any): void {
    this.value = value;
  }

  public registerOnChange(fn: any): void {
    this._onChange = fn;
  }

  public registerOnTouched(fn: any): void {
    this._onTouched = fn;
  }

  @HostListener('input', ['$event.target.value'])
  public onInput(value: any): void {
    if (!this.readonly && !this.disabled) {
      this.value = value;
      this.valueChange.emit(value);
      this._onChange(value);
      this._onTouched();
    }
  }

  @HostListener('focus', ['true'])
  @HostListener('blur', ['false'])
  public onToggleFocus(focused: boolean): void {
    if (!this.readonly && !this.disabled) {
      this._focused = focused;
    }
  }

  protected _onChange = (_: any): void => {
  };

  protected _onTouched = (): void => {
  };
}

